import React, { useState, useEffect } from 'react';
import Navigation from '../common/Navigation';
import RequestEquipment from './RequestEquipment';
import ViewInventory from './ViewInventory';
import ReturnEquipment from './ReturnEquipment';
import { officerAPI } from '../../utils/api';
import { toast } from 'react-toastify';
import './OfficerDashboard.css';

const OfficerDashboard = () => {
  const [activeSection, setActiveSection] = useState('dashboard');
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showCancelModal, setShowCancelModal] = useState(false);
  const [selectedRequestId, setSelectedRequestId] = useState(null);

  const sections = [
    'dashboard',
    'requestEquipment',
    'viewInventory',
    'returnEquipment',
    'myRequests'
  ];

  useEffect(() => {
    if (activeSection === 'dashboard') {
      fetchDashboardData();
    }
  }, [activeSection]);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      const response = await officerAPI.getDashboard();
      if (response.data.success) {
        setDashboardData(response.data.data);
      }
    } catch (error) {
      toast.error('Failed to fetch dashboard data');
      console.error('Dashboard fetch error:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCancelRequestClick = (requestId) => {
    setSelectedRequestId(requestId);
    setShowCancelModal(true);
  };

  const confirmCancelRequest = async () => {
    try {
      await officerAPI.cancelRequest(selectedRequestId);
      toast.success('Request cancelled successfully');
      fetchDashboardData();
      setShowCancelModal(false);
      setSelectedRequestId(null);
    } catch (error) {
      toast.error('Failed to cancel request');
      console.error('Cancel request error:', error);
    }
  };

  const renderMainContent = () => {
    if (loading && activeSection === 'dashboard') {
      return (
        <div className="loading-state">
          <div className="spinner-large"></div>
          <p>Loading dashboard...</p>
        </div>
      );
    }

    switch (activeSection) {
      case 'dashboard':
        return (
          <div className="dashboard-section">
            <h2>Dashboard</h2>
            {dashboardData && (
              <div className="dashboard-stats">
                <div className="stat-card">
                  <h3>{dashboardData.totalRequests}</h3>
                  <p>Total Requests</p>
                </div>
                <div className="stat-card">
                  <h3>{dashboardData.pendingRequests}</h3>
                  <p>Pending</p>
                </div>
                <div className="stat-card">
                  <h3>{dashboardData.approvedRequests}</h3>
                  <p>Approved</p>
                </div>
              </div>
            )}

            <h3 style={{ marginTop: '30px' }}>Your Recent Requests</h3>
            {dashboardData?.recentRequests?.length === 0 ? (
              <p>You haven't made any equipment requests yet.</p>
            ) : (
              <div className="requests-list">
                {dashboardData?.recentRequests?.map((request) => (
                  <div key={request._id} className="request-item">
                    <div className="request-info">
                      <h4>{request.equipmentId?.model}</h4>
                      <p>{request.equipmentId?.serialNumber}</p>
                      <small>
                        {request.requestType} â€¢ {new Date(request.createdAt).toLocaleDateString()}
                      </small>
                    </div>
                    <span className={`badge badge-${request.status.toLowerCase()}`}>
                      {request.status}
                    </span>
                    {request.status === 'Pending' && (
                      <button
                        onClick={() => handleCancelRequestClick(request._id)}
                        className="btn btn-danger btn-sm"
                      >
                        Cancel Request
                      </button>
                    )}
                  </div>
                ))}
              </div>
            )}
          </div>
        );
      case 'requestEquipment':
        return <RequestEquipment onRequestSubmitted={fetchDashboardData} />;
      case 'viewInventory':
        return <ViewInventory />;
      case 'returnEquipment':
        return <ReturnEquipment />;
      case 'myRequests':
        return (
          <div className="my-requests-section">
            <h2>My Requests</h2>
            <p>View and manage your equipment requests here.</p>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="officer-dashboard">
      <Navigation />
      <div className="dashboard-container">
        <div className="sidebar">
          <div className="sidebar-menu">
            {sections.map((section) => (
              <button
                key={section}
                className={`menu-item ${activeSection === section ? 'active' : ''}`}
                onClick={() => setActiveSection(section)}
              >
                {section === 'dashboard' && 'Dashboard'}
                {section === 'requestEquipment' && 'Request Equipment'}
                {section === 'viewInventory' && 'View Inventory'}
                {section === 'returnEquipment' && 'Return Equipment'}
                {section === 'myRequests' && 'My Requests'}
              </button>
            ))}
          </div>
        </div>
        <div className="main-content">
          {renderMainContent()}
        </div>
      </div>

      {showCancelModal && (
        <CancelConfirmationModal
          onConfirm={confirmCancelRequest}
          onCancel={() => {
            setShowCancelModal(false);
            setSelectedRequestId(null);
          }}
        />
      )}
    </div>
  );
};

const CancelConfirmationModal = ({ onConfirm, onCancel }) => {
  return (
    <div className="modal-overlay" onClick={onCancel}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h3>Cancel Request</h3>
          <button onClick={onCancel} className="close-btn">&times;</button>
        </div>

        <div className="modal-body">
          <p>Are you sure you want to cancel this request?</p>
        </div>

        <div className="modal-actions">
          <button onClick={onCancel} className="btn btn-secondary">
            No
          </button>
          <button onClick={onConfirm} className="btn btn-danger">
            Yes, Cancel Request
          </button>
        </div>
      </div>
    </div>
  );
};

export default OfficerDashboard;
