import React, { useState, useEffect } from 'react';
import { adminAPI } from '../../utils/api';
import { toast } from 'react-toastify';

// Valid Indian state codes
const STATE_CODES = [
  { code: 'IND', name: 'All-India' },
  { code: 'AP', name: 'Andhra Pradesh' },
  { code: 'AR', name: 'Arunachal Pradesh' },
  { code: 'AS', name: 'Assam' },
  { code: 'BR', name: 'Bihar' },
  { code: 'CG', name: 'Chhattisgarh' },
  { code: 'GA', name: 'Goa' },
  { code: 'GJ', name: 'Gujarat' },
  { code: 'HR', name: 'Haryana' },
  { code: 'HP', name: 'Himachal Pradesh' },
  { code: 'JH', name: 'Jharkhand' },
  { code: 'KA', name: 'Karnataka' },
  { code: 'KL', name: 'Kerala' },
  { code: 'MP', name: 'Madhya Pradesh' },
  { code: 'MH', name: 'Maharashtra' },
  { code: 'MN', name: 'Manipur' },
  { code: 'ML', name: 'Meghalaya' },
  { code: 'MZ', name: 'Mizoram' },
  { code: 'NL', name: 'Nagaland' },
  { code: 'OD', name: 'Odisha' },
  { code: 'PB', name: 'Punjab' },
  { code: 'RJ', name: 'Rajasthan' },
  { code: 'SK', name: 'Sikkim' },
  { code: 'TN', name: 'Tamil Nadu' },
  { code: 'TG', name: 'Telangana' },
  { code: 'TR', name: 'Tripura' },
  { code: 'UP', name: 'Uttar Pradesh' },
  { code: 'UK', name: 'Uttarakhand' },
  { code: 'WB', name: 'West Bengal' },
  { code: 'AN', name: 'Andaman & Nicobar' },
  { code: 'CH', name: 'Chandigarh' },
  { code: 'DH', name: 'Dadra & Nagar Haveli' },
  { code: 'DD', name: 'Daman & Diu' },
  { code: 'DL', name: 'Delhi' },
  { code: 'JK', name: 'Jammu & Kashmir' },
  { code: 'LA', name: 'Ladakh' },
  { code: 'LD', name: 'Lakshadweep' },
  { code: 'PY', name: 'Puducherry' }
];

// Valid rank codes
const RANK_CODES = [
  { code: 'PC', name: 'Police Constable' },
  { code: 'HC', name: 'Head Constable' },
  { code: 'ASI', name: 'Assistant Sub-Inspector' },
  { code: 'SI', name: 'Sub-Inspector' },
  { code: 'PSI', name: 'Police Sub-Inspector' },
  { code: 'INSP', name: 'Inspector' },
  { code: 'PI', name: 'Police Inspector' },
  { code: 'DSP', name: 'Deputy Superintendent of Police' },
  { code: 'SP', name: 'Superintendent of Police' },
  { code: 'SSP', name: 'Senior Superintendent of Police' },
  { code: 'DCP', name: 'Deputy Commissioner of Police' },
  { code: 'ADDCP', name: 'Additional DCP' },
  { code: 'JCP', name: 'Joint Commissioner of Police' },
  { code: 'IGP', name: 'Inspector General of Police' },
  { code: 'DIGP', name: 'Deputy IGP' },
  { code: 'ADGP', name: 'Additional DGP' },
  { code: 'DGP', name: 'Director General of Police' },
  { code: 'IPS', name: 'Indian Police Service' }
];

const UserManagement = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [searchTerm, setSearchTerm] = useState('');
  const [roleFilter, setRoleFilter] = useState('');

  const [officerIdParts, setOfficerIdParts] = useState({
    state: '',
    rankCode: '',
    year: new Date().getFullYear().toString(),
    serial: ''
  });

  const [newUser, setNewUser] = useState({
    officerId: '',
    fullName: '',
    designation: '',
    email: '',
    dateOfJoining: '',
    password: '',
    rank: ''
  });

  // Rank categories with their designations
  const rankCategories = {
    'Senior Command (DGP, SP, DCP)': [
      'Director General of Police (DGP)',
      'Superintendent of Police (SP)',
      'Deputy Commissioner of Police (DCP)'
    ],
    'District Administrator (DSP)': [
      'Deputy Superintendent of Police (DSP)'
    ],
    'Police Station Officers (PI, SI, PSI)': [
      'Police Inspector (PI)',
      'Sub-Inspector (SI)',
      'Police Sub-Inspector (PSI)'
    ],
    'Police Station Staff (HC, PC)': [
      'Head Constable (HC)',
      'Police Constable (PC)'
    ]
  };

  // Generate Officer ID from parts
  const generateOfficerId = () => {
    const { state, rankCode, year, serial } = officerIdParts;
    if (state && rankCode && year && serial) {
      const paddedSerial = serial.padStart(4, '0');
      return `${state}/${rankCode}/${year}/${paddedSerial}`;
    }
    return '';
  };

  // Update Officer ID when parts change
  useEffect(() => {
    const generatedId = generateOfficerId();
    setNewUser(prev => ({ ...prev, officerId: generatedId }));
  }, [officerIdParts]);

  useEffect(() => {
    fetchUsers();
  }, [currentPage, searchTerm, roleFilter]);

  const fetchUsers = async () => {
    try {
      setLoading(true);
      const response = await adminAPI.getUsers({
        page: currentPage,
        limit: 10,
        search: searchTerm,
        role: roleFilter
      });

      if (response.data.success) {
        setUsers(response.data.data.users);
        setTotalPages(response.data.data.pagination.pages);
      }
    } catch (error) {
      toast.error('Failed to fetch users');
    } finally {
      setLoading(false);
    }
  };

  const handleToggleStatus = async (userId, currentStatus) => {
    try {
      await adminAPI.updateUser(userId, { isActive: !currentStatus });
      toast.success(`User ${!currentStatus ? 'activated' : 'deactivated'} successfully`);
      fetchUsers();
    } catch (error) {
      toast.error('Failed to update user status');
    }
  };

  const handleCreateUser = async (e) => {
    e.preventDefault();
    try {
      const response = await adminAPI.createUser(newUser);
      if (response.data.success) {
        toast.success('Officer created successfully');
        setShowCreateModal(false);
        setNewUser({
          officerId: '',
          fullName: '',
          designation: '',
          email: '',
          dateOfJoining: '',
          password: '',
          rank: ''
        });
        setOfficerIdParts({
          state: '',
          rankCode: '',
          year: new Date().getFullYear().toString(),
          serial: ''
        });
        fetchUsers();
      }
    } catch (error) {
      toast.error(error.response?.data?.message || 'Failed to create officer');
    }
  };

  const handleRankChange = (e) => {
    const selectedRank = e.target.value;
    setNewUser({
      ...newUser,
      rank: selectedRank,
      designation: '' // Reset designation when rank changes
    });
  };

  const getAvailableDesignations = () => {
    if (!newUser.rank) return [];
    return rankCategories[newUser.rank] || [];
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading users...</p>
      </div>
    );
  }

  return (
    <div className="user-management">
      <div className="section-header">
        <h2>User Management</h2>
        <button 
          className="btn btn-primary"
          onClick={() => setShowCreateModal(true)}
        >
          + Create New Officer
        </button>
      </div>

      {/* Filters Section */}
      <div className="search-filters">
        <input
          type="text"
          placeholder="Search by name, email, or officer ID..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="search-input"
        />
        <select
          value={roleFilter}
          onChange={(e) => setRoleFilter(e.target.value)}
          className="filter-select"
        >
          <option value="">All Roles</option>
          <option value="admin">Admin</option>
          <option value="officer">Officer</option>
        </select>
      </div>

      {/* Users Table */}
      {users.length === 0 ? (
        <div className="empty-state">
          <p>No users found.</p>
        </div>
      ) : (
        <div className="table-container">
          <table className="users-table">
            <thead>
              <tr>
                <th>Officer ID</th>
                <th>Full Name</th>
                <th>Email</th>
                <th>Designation</th>
                <th>Rank</th>
                <th>Years of Service</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {users.map(user => (
                <tr key={user._id}>
                  <td><code style={{fontSize: '0.85em'}}>{user.officerId}</code></td>
                  <td>{user.fullName}</td>
                  <td>{user.email}</td>
                  <td>
                    <span className="badge badge-info">{user.designation}</span>
                  </td>
                  <td>{user.rank}</td>
                  <td>{user.yearsOfService || 'N/A'}</td>
                  <td>
                    <span className={`badge ${user.isActive ? 'badge-success' : 'badge-error'}`}>
                      {user.isActive ? 'Active' : 'Inactive'}
                    </span>
                  </td>
                  <td>
                    <button
                      className={`btn btn-sm ${user.isActive ? 'btn-warning' : 'btn-success'}`}
                      onClick={() => handleToggleStatus(user._id, user.isActive)}
                    >
                      {user.isActive ? 'Deactivate' : 'Activate'}
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {/* Pagination */}
      {totalPages > 1 && (
        <div className="pagination">
          <button
            className="btn btn-secondary"
            onClick={() => setCurrentPage(prev => Math.max(1, prev - 1))}
            disabled={currentPage === 1}
          >
            Previous
          </button>
          <span className="page-info">
            Page {currentPage} of {totalPages}
          </span>
          <button
            className="btn btn-secondary"
            onClick={() => setCurrentPage(prev => Math.min(totalPages, prev + 1))}
            disabled={currentPage === totalPages}
          >
            Next
          </button>
        </div>
      )}

      {/* Create User Modal */}
      {showCreateModal && (
        <div className="modal-overlay" onClick={() => setShowCreateModal(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>Create New Officer</h3>
              <button 
                className="modal-close"
                onClick={() => setShowCreateModal(false)}
              >
                Ã—
              </button>
            </div>
            
            <form onSubmit={handleCreateUser} className="modal-form">
              {/* Officer ID Builder Section */}
              <div className="form-section">
                <h4 style={{marginBottom: '12px', fontSize: '14px', fontWeight: '600'}}>
                  Officer ID Builder
                </h4>
                <div className="form-row" style={{gap: '8px'}}>
                  <div className="form-group" style={{flex: '1'}}>
                    <label>State *</label>
                    <select
                      value={officerIdParts.state}
                      onChange={(e) => setOfficerIdParts({...officerIdParts, state: e.target.value})}
                      required
                    >
                      <option value="">Select State</option>
                      {STATE_CODES.map(s => (
                        <option key={s.code} value={s.code}>{s.code} - {s.name}</option>
                      ))}
                    </select>
                  </div>

                  <div className="form-group" style={{flex: '1'}}>
                    <label>Rank Code *</label>
                    <select
                      value={officerIdParts.rankCode}
                      onChange={(e) => setOfficerIdParts({...officerIdParts, rankCode: e.target.value})}
                      required
                    >
                      <option value="">Select Rank</option>
                      {RANK_CODES.map(r => (
                        <option key={r.code} value={r.code}>{r.code} - {r.name}</option>
                      ))}
                    </select>
                  </div>

                  <div className="form-group" style={{flex: '0.7'}}>
                    <label>Year *</label>
                    <input
                      type="number"
                      value={officerIdParts.year}
                      onChange={(e) => setOfficerIdParts({...officerIdParts, year: e.target.value})}
                      placeholder="2024"
                      required
                      min="1947"
                      max={new Date().getFullYear() + 1}
                    />
                  </div>

                  <div className="form-group" style={{flex: '0.7'}}>
                    <label>Serial *</label>
                    <input
                      type="number"
                      value={officerIdParts.serial}
                      onChange={(e) => setOfficerIdParts({...officerIdParts, serial: e.target.value})}
                      placeholder="0001"
                      required
                      min="1"
                      max="999999"
                    />
                  </div>
                </div>
                
                {/* Preview Officer ID */}
                <div style={{
                  marginTop: '12px',
                  padding: '12px',
                  background: 'var(--color-secondary)',
                  borderRadius: 'var(--radius-base)',
                  fontFamily: 'monospace',
                  fontSize: '14px',
                  fontWeight: '600',
                  color: 'var(--color-primary)'
                }}>
                  Officer ID: {newUser.officerId || 'STATE/RANK/YEAR/SERIAL'}
                </div>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>Full Name *</label>
                  <input
                    type="text"
                    value={newUser.fullName}
                    onChange={(e) => setNewUser({...newUser, fullName: e.target.value})}
                    placeholder="Enter full name"
                    required
                    minLength="3"
                    maxLength="100"
                  />
                </div>

                <div className="form-group">
                  <label>Email *</label>
                  <input
                    type="email"
                    value={newUser.email}
                    onChange={(e) => setNewUser({...newUser, email: e.target.value})}
                    placeholder="officer@police.gov.in"
                    required
                  />
                </div>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>Date of Joining *</label>
                  <input
                    type="date"
                    value={newUser.dateOfJoining}
                    onChange={(e) => setNewUser({...newUser, dateOfJoining: e.target.value})}
                    required
                    max={new Date().toISOString().split('T')[0]}
                  />
                </div>

                <div className="form-group">
                  <label>Rank Category *</label>
                  <select
                    value={newUser.rank}
                    onChange={handleRankChange}
                    required
                  >
                    <option value="">Select Rank Category</option>
                    {Object.keys(rankCategories).map(rank => (
                      <option key={rank} value={rank}>{rank}</option>
                    ))}
                  </select>
                </div>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>Designation *</label>
                  <select
                    value={newUser.designation}
                    onChange={(e) => setNewUser({...newUser, designation: e.target.value})}
                    required
                    disabled={!newUser.rank}
                  >
                    <option value="">
                      {newUser.rank ? 'Select Designation' : 'Select Rank First'}
                    </option>
                    {getAvailableDesignations().map(designation => (
                      <option key={designation} value={designation}>
                        {designation}
                      </option>
                    ))}
                  </select>
                </div>

                <div className="form-group">
                  <label>Password *</label>
                  <input
                    type="password"
                    value={newUser.password}
                    onChange={(e) => setNewUser({...newUser, password: e.target.value})}
                    placeholder="Minimum 8 characters"
                    required
                    minLength="8"
                  />
                </div>
              </div>

              <div className="modal-actions">
                <button 
                  type="button" 
                  className="btn btn-secondary"
                  onClick={() => setShowCreateModal(false)}
                >
                  Cancel
                </button>
                <button type="submit" className="btn btn-primary">
                  Create Officer
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default UserManagement;
