import React, { useState, useEffect } from 'react';
import { equipmentAPI } from '../../utils/api';
import { toast } from 'react-toastify';

// ======== 游릭 ADDED THIS HELPER 游릭 ========
const formatDateTime = (dateString) => {
  if (!dateString) return 'N/A';
  return new Date(dateString).toLocaleString('en-IN');
};

const EquipmentLedger = () => {
  // ... (state is unchanged)
  const [pools, setPools] = useState([]);
  const [selectedPool, setSelectedPool] = useState(null);
  const [selectedItemId, setSelectedItemId] = useState('');
  const [historyData, setHistoryData] = useState(null);
  const [loadingPools, setLoadingPools] = useState(true);
  const [loadingHistory, setLoadingHistory] = useState(false);

  // ... (useEffect, fetchPools, handlePoolChange are unchanged) ...
  useEffect(() => {
    fetchPools();
  }, []);

  const fetchPools = async () => { /* ... */ };
  const handlePoolChange = (e) => { /* ... */ };

  const handleItemChange = async (e) => {
    const uniqueId = e.target.value;
    setSelectedItemId(uniqueId);

    if (!uniqueId) {
      setHistoryData(null);
      return;
    }

    try {
      setLoadingHistory(true);
      const response = await equipmentAPI.getItemHistory(selectedPool._id, uniqueId);
      if (response.data.success) {
        setHistoryData(response.data.data); // This now contains the 'timeline'
      }
    } catch (error) {
      toast.error('Failed to fetch item history');
      setHistoryData(null);
    } finally {
      setLoadingHistory(false);
    }
  };

  return (
    <div className="equipment-ledger">
      {/* ... (header and select dropdowns are unchanged) ... */}
      <div className="management-header">
        <h3>Equipment Ledger</h3>
        <p>Select a pool and item to view its complete history.</p>
        <div className="search-filters">
          <select
            onChange={handlePoolChange}
            value={selectedPool?._id || ''}
            className="form-control"
            disabled={loadingPools}
          >
            <option value="">-- Select an Equipment Pool --</option>
            {pools.map(pool => (
              <option key={pool._id} value={pool._id}>
                {pool.poolName} ({pool.model})
              </option>
            ))}
          </select>

          <select
            onChange={handleItemChange}
            value={selectedItemId}
            className="form-control"
            disabled={!selectedPool}
          >
            <option value="">-- Select an Item ID --</option>
            {selectedPool?.items.map(item => (
              <option key={item.uniqueId} value={item.uniqueId}>
                {item.uniqueId} ({item.status})
              </option>
            ))}
          </select>
        </div>
      </div>

      {loadingHistory ? (
        <div className="loading-container">
          <div className="spinner"></div>
          <p>Loading item history...</p>
        </div>
      ) : historyData && (
        // ======== 游릭 MODIFIED THIS SECTION 游릭 ========
        <div className="history-details">
          <div className="dashboard-card">
            <h4>Item Timeline for {historyData.uniqueId}</h4>
            {historyData.timeline.length === 0 ? (
              <p>No history found for this item.</p>
            ) : (
              <table className="table">
                <thead>
                  <tr>
                    <th>Date & Time</th>
                    <th>Event</th>
                    <th>Details</th>
                    <th>Condition / Status</th>
                  </tr>
                </thead>
                <tbody>
                  {historyData.timeline.map(entry => {
                    let eventType, details, condition;
                        
                    if (entry.eventType === 'Usage') {
                      eventType = <span className="badge badge-info">Issued</span>;
                      details = `To: ${entry.officer} (${entry.purpose})`;
                      condition = `(at issue: ${entry.conditionAtIssue})`;
                    } else if (entry.eventType === 'Return') {
                      eventType = <span className="badge badge-success">Returned</span>;
                      details = `By: ${entry.officerName || 'N/A'}`;
                      condition = (
                        <span className={`badge badge-${entry.conditionAtReturn === 'Good' || entry.conditionAtReturn === 'Excellent' ? 'success' : 'danger'}`}>
                          {entry.conditionAtReturn}
                        </span>
                      );
                    } else { // Maintenance
                      eventType = <span className="badge badge-warning">Maintenance</span>;
                      details = entry.details;
                      condition = `(By: ${entry.performedBy})`;
                    }
                    
                    return (
                      <tr key={entry._id}>
                        <td>{formatDateTime(entry.eventDate)}</td>
                        <td>{eventType}</td>
                        <td>{details}</td>
                        <td>{condition}</td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            )}
          </div>
        </div>
        // ============================================
      )}
    </div>
  );
};

export default EquipmentLedger;
