import React, { useState, useEffect } from 'react';
import Navigation from '../common/Navigation';
import RequestEquipment from './RequestEquipment';
import ViewInventory from './ViewInventory';
import ReturnEquipment from './ReturnEquipment';
import { officerAPI } from '../../utils/api';
import { toast } from 'react-toastify';
// UI/UX Enhancement: The CSS file is the primary source of the new design.
import './OfficerDashboard.css';

const OfficerDashboard = () => {
  const [activeSection, setActiveSection] = useState('dashboard');
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);

  const sections = [
    'dashboard',
    'requestEquipment',
    'viewInventory',
    'returnEquipment',
    'myRequests'
  ];

  useEffect(() => {
    if (activeSection === 'dashboard') {
      fetchDashboardData();
    }
  }, [activeSection]);

  const fetchDashboardData = async () => {
    /* UI/UX Enhancement: The 'loading' state is now styled 
       globally via .loading-state in OfficerDashboard.css.
       Toast notifications are also styled via the new theme.
    */
    try {
      setLoading(true);
      const response = await officerAPI.getDashboard();
      if (response.data.success) {
        setDashboardData(response.data.data);
      }
    } catch (error) {
      toast.error('Failed to fetch dashboard data');
      console.error('Dashboard fetch error:', error);
    } finally {
      setLoading(false);
    }
  };

  const renderMainContent = () => {
    if (loading && activeSection === 'dashboard') {
      return (
        /* UI/UX Enhancement: This component is now styled by .loading-state */
        <div className="loading-state">
          <div className="spinner-large"></div>
          <p>Loading dashboard...</p>
        </div>
      );
    }

    switch (activeSection) {
      case 'dashboard':
        return <OfficerDashboardStats data={dashboardData} />;
      case 'requestEquipment':
        return <RequestEquipment onRequestSubmitted={fetchDashboardData} />;
      case 'viewInventory':
        return <ViewInventory />;
      case 'returnEquipment':
        return <ReturnEquipment onEquipmentReturned={fetchDashboardData} />;
      case 'myRequests':
        return <MyRequests />;
      default:
        return <OfficerDashboardStats data={dashboardData} />;
    }
  };

  return (
    // UI/UX Enhancement: Root class .officer-dashboard is styled in CSS
    <div className="officer-dashboard">
      <Navigation 
        activeSection={activeSection}
        setActiveSection={setActiveSection}
        sections={sections}
      />
      <div className="content-wrapper">
        <div className="main-content">
          {renderMainContent()}
        </div>
      </div>
    </div>
  );
};

const OfficerDashboardStats = ({ data }) => {
  if (!data) {
    return <div>Loading...</div>;
  }

  return (
    <div className="dashboard-stats-container">
      <h2>Dashboard</h2>
      <div className="stats-grid">
        <StatCard 
          icon="ðŸ“Š" 
          value={data?.totalRequests || 0} 
          title="Total Requests" 
          subtitle="All time"
        />
        <StatCard 
          icon="â³" 
          value={data?.pendingRequests || 0} 
          title="Pending Requests" 
          subtitle="Awaiting approval"
        />
        <StatCard 
          icon="âœ…" 
          value={data?.approvedRequests || 0} 
          title="Approved" 
          subtitle="Ready for pickup"
        />
      </div>
      
      <div className="recent-requests-container">
        <h3>Recent Activity</h3>
        {data?.recentRequests && data.recentRequests.length > 0 ? (
          <div className="recent-requests-list">
            {data.recentRequests.map((request) => (
              <div key={request._id} className="recent-request-item">
                <div className="request-details">
                  <p className="equipment-name">{request.equipmentId?.name || 'N/A'}</p>
                  <p className="request-type">{request.requestType} â€¢ {new Date(request.createdAt).toLocaleDateString()}</p>
                </div>
                <span className={`badge badge-${request.status.toLowerCase()}`}>
                  {request.status}
                </span>
              </div>
            ))}
          </div>
        ) : (
          <p>No recent requests</p>
        )}
      </div>
    </div>
  );
};

const MyRequests = () => {
  const [requests, setRequests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [showCancelModal, setShowCancelModal] = useState(false);
  const [selectedRequestId, setSelectedRequestId] = useState(null);

  useEffect(() => {
    fetchRequests();
  }, [currentPage]);

  const fetchRequests = async () => {
    try {
      setLoading(true);
      const response = await officerAPI.getRequests({
        page: currentPage,
        limit: 10
      });

      if (response.data.success) {
        setRequests(response.data.data.requests);
        setTotalPages(response.data.data.pagination.pages);
      }
    } catch (error) {
      toast.error('Failed to fetch requests');
      console.error('Fetch requests error:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCancelRequestClick = (requestId) => {
    setSelectedRequestId(requestId);
    setShowCancelModal(true);
  };

  const confirmCancelRequest = async () => {
    try {
      await officerAPI.cancelRequest(selectedRequestId);
      toast.success('Request cancelled successfully');
      fetchRequests();
      setShowCancelModal(false);
      setSelectedRequestId(null);
    } catch (error) {
      toast.error('Failed to cancel request');
      console.error('Cancel request error:', error);
    }
  };

  if (loading) {
    return (
      <div className="loading-state">
        <div className="spinner-large"></div>
        <p>Loading requests...</p>
      </div>
    );
  }

  return (
    <>
      <div className="my-requests">
        <h2>My Requests</h2>

        {requests.length === 0 ? (
          <div className="no-data">
            <p>You haven't made any equipment requests yet.</p>
          </div>
        ) : (
          <>
            <div className="requests-list">
              {requests.map((request) => (
                <div key={request._id} className="request-card">
                  <div className="request-header">
                    <h4>{request.equipmentId?.name || 'Unknown Equipment'}</h4>
                    <span className={`badge badge-${request.status.toLowerCase()}`}>
                      {request.status}
                    </span>
                  </div>

                  <div className="request-body">
                    <div className="detail-row">
                      <span className="label">Type:</span>
                      <span className="value">{request.requestType}</span>
                    </div>
                    <div className="detail-row">
                      <span className="label">Priority:</span>
                      <span className="value">{request.priority}</span>
                    </div>
                    <div className="detail-row">
                      <span className="label">Requested:</span>
                      <span className="value">{new Date(request.createdAt).toLocaleDateString()}</span>
                    </div>
                    {request.reason && (
                      <div className="detail-row">
                        <span className="label">Reason:</span>
                        <p>{request.reason}</p>
                      </div>
                    )}
                  </div>

                  {request.status === 'Pending' && (
                    <div className="request-actions">
                      {/* UI/UX Enhancement: Buttons are styled by .btn */}
                      <button
                        onClick={() => handleCancelRequestClick(request._id)}
                        className="btn btn-danger btn-sm"
                      >
                        Cancel Request
                      </button>
                    </div>
                  )}
                </div>
              ))}
            </div>

            {totalPages > 1 && (
              <div className="pagination">
                <button
                  onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                  disabled={currentPage === 1}
                  className="btn btn-secondary"
                >
                  Previous
                </button>
                <span>Page {currentPage} of {totalPages}</span>
                <button
                  onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
                  disabled={currentPage === totalPages}
                  className="btn btn-secondary"
                >
                  Next
                </button>
              </div>
            )}
          </>
        )}
      </div>

      {showCancelModal && (
        <CancelRequestModal
          onConfirm={confirmCancelRequest}
          onCancel={() => {
            setShowCancelModal(false);
            setSelectedRequestId(null);
          }}
        />
      )}
    </>
  );
};

const CancelRequestModal = ({ onConfirm, onCancel }) => {
  return (
    <div className="modal-overlay" onClick={onCancel}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h3>Cancel Request</h3>
          <button onClick={onCancel} className="close-btn">&times;</button>
        </div>
        <div className="modal-body">
          <p>Are you sure you want to cancel this request?</p>
        </div>
        <div className="modal-actions">
          <button onClick={onCancel} className="btn btn-secondary">
            No
          </button>
          <button onClick={onConfirm} className="btn btn-danger">
            Yes, Cancel Request
          </button>
        </div>
      </div>
    </div>
  );
};

const StatCard = ({ icon, value, title, subtitle }) => (
  <div className="stat-card">
    <div className="stat-icon">{icon}</div>
    <div className="stat-value">{value}</div>
    <div className="stat-title">{title}</div>
    <div className="stat-subtitle">{subtitle}</div>
  </div>
);

const formatSectionTitle = (section) => {
  const titles = {
    dashboard: 'Officer Dashboard',
    requestEquipment: 'Request Equipment',
    viewInventory: 'Equipment Inventory',
    returnEquipment: 'Return Equipment',
    myRequests: 'My Requests'
  };
  return titles[section] || section;
};

export default OfficerDashboard;
