import React, { useState, useEffect } from 'react';
import { officerAPI } from '../../utils/api';
import { toast } from 'react-toastify';

const MyHistory = () => {
  const [history, setHistory] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchHistory();
  }, []);

  const fetchHistory = async () => {
    try {
      setLoading(true);
      const response = await officerAPI.getMyHistory();
      if (response.data.success) {
        setHistory(response.data.data.history);
      }
    } catch (error) {
      toast.error('Failed to fetch equipment history');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading your history...</p>
      </div>
    );
  }

  return (
    <div className="my-history">
      <div className="inventory-header">
        <h3>My Equipment Usage History</h3>
        <p>A complete log of all equipment items you have used.</p>
      </div>

      {history.length === 0 ? (
        <div className="no-data">
          <p>You have no equipment usage history on file.</p>
        </div>
      ) : (
        <div className="inventory-table">
          <table className="table">
            <thead>
              <tr>
                <th>Item</th>
                <th>Request ID</th>
                <th>Reason</th>
                <th>Requested</th>
                <th>Approved (Issued)</th>
                <th>Returned</th>
                <th>Condition (Return)</th>
              </tr>
            </thead>
            <tbody>
              {history.map((entry) => (
                <tr key={entry.recordId || entry._id}>
                  <td>{entry.equipmentPoolName} ({entry.itemUniqueId})</td>
                  <td>{entry.requestId || 'N/A'}</td>
                  <td>{entry.purpose}</td>
                  <td>{entry.requestDate ? new Date(entry.requestDate).toLocaleString() : 'N/A'}</td>
                  <td>{new Date(entry.issuedDate).toLocaleString()}</td>
                  <td>{entry.returnedDate ? new Date(entry.returnedDate).toLocaleString() : 'Pending Return'}</td>
                  <td>
                    <span className={`badge badge-${entry.conditionAtReturn === 'Good' || entry.conditionAtReturn === 'Excellent' ? 'success' : 'warning'}`}>
                      {entry.conditionAtReturn || 'N/A'}
                    </span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default MyHistory;
