import React, { useState, useEffect } from 'react';
import { equipmentAPI } from '../../utils/api';
import { toast } from 'react-toastify';

// A simple helper to format dates
const formatDateTime = (dateString) => {
  if (!dateString) return 'N/A';
  return new Date(dateString).toLocaleString('en-IN');
};

const ItemHistoryModal = ({ poolId, uniqueId, onClose }) => {
  // 游릭 MODIFIED: Initialize state correctly to prevent crashes
  const [timeline, setTimeline] = useState([]); 
  const [itemDetails, setItemDetails] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchHistory = async () => {
      try {
        setLoading(true);
        const response = await equipmentAPI.getItemHistory(poolId, uniqueId);
        if (response.data.success) {
          // Set state from the API, providing a default empty array for timeline
          setTimeline(response.data.data.timeline || []); 
          setItemDetails({
            currentStatus: response.data.data.currentStatus,
            currentCondition: response.data.data.currentCondition
          });
        } else {
          toast.error(response.data.message);
        }
      } catch (error) {
        toast.error('Failed to fetch item history');
      } finally {
        setLoading(false);
      }
    };
    
    fetchHistory();
  }, [poolId, uniqueId]);

  // This function renders the correct badge based on the event
  const renderCondition = (entry) => {
    if (entry.eventType === 'Usage') {
      return `(at issue: ${entry.conditionAtIssue})`;
    }
    if (entry.eventType === 'Return') {
      const isGood = entry.condition === 'Good' || entry.condition === 'Excellent';
      return (
        <span className={`badge badge-${isGood ? 'success' : 'danger'}`}>
          {entry.condition}
        </span>
      );
    }
    // for Maintenance
    return entry.condition; 
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content equipment-details-modal" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h3>History for Item: {uniqueId}</h3>
          <button onClick={onClose} className="close-btn">&times;</button>
        </div>
        <div className="modal-body">
          {loading ? (
            <div className="loading-container">
              <div className="spinner"></div>
              <p>Loading history...</p>
            </div>
          ) : !itemDetails ? ( // This check is safe
            <p>Could not load history.</p>
          ) : (
            <>
              <div className="detail-section">
                <h4>Current Status</h4>
                <div className="detail-grid">
                  <div className="detail-item">
                    <strong>Status:</strong> {itemDetails.currentStatus}
                  </div>
                  <div className="detail-item">
                    <strong>Condition:</strong> {itemDetails.currentCondition}
                  </div>
                </div>
              </div>

              {/* ======== 游릭 MODIFIED THIS ENTIRE SECTION 游릭 ======== */}
              {/* This now reads from the single 'timeline' array */}
              <div className="detail-section">
                <h4>Item Timeline</h4>
                {/* This check on 'timeline.length' is now safe */}
                {timeline.length === 0 ? ( 
                  <p>No history found for this item. (Brand New)</p>
                ) : (
                  <table className="table table-sm">
                    <thead>
                      <tr>
                        <th>Date & Time</th>
                        <th>Event</th>
                        <th>Details</th>
                        <th>Condition</th>
                      </tr>
                    </thead>
                    <tbody>
                      {timeline.map(entry => (
                        <tr key={entry._id}>
                          <td>{formatDateTime(entry.eventDate)}</td>
                          <td>
                            {/* This shows a colored badge for the event type */}
                            <span className={`badge badge-${
                              entry.eventType === 'Usage' ? 'info' :
                              entry.eventType === 'Return' ? 'success' : 'warning'
                            }`}>
                              {entry.eventType}
                            </span>
                          </td>
                          <td title={entry.details}>{entry.details}</td>
                          <td>{renderCondition(entry)}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                )}
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default ItemHistoryModal;
