import react, { useState, useEffect } from 'react';
import { adminAPI } from '../../utils/api';
import { toast } from 'react-toastify';
/* UI/UX Enhancement: This component is now styled by AdminDashboard.css.
   - Filters (.search-filters) and create button (.btn-primary) are themed.
   - The table (.users-table) is more professional.
   - Badges (.badge) for Role and Status are now properly styled.
   - The "Create New Officer" modal is significantly enhanced.
*/

const UserManagement = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [searchTerm, setSearchTerm] = useState('');
  const [roleFilter, setRoleFilter] = useState('');

  const [newUser, setNewUser] = useState({
    officerId: '',
    fullName: '',
    designation: '',
    email: '',
    dateOfJoining: '',
    password: '',
    rank: ''
  });

  // Rank categories with their designations
  const rankCategories = {
    'Senior Command (DGP, SP, DCP)': [
      'Director General of Police (DGP)',
      'Superintendent of Police (SP)',
      'Deputy Commissioner of Police (DCP)'
    ],
    'District Administrator (DSP)': [
      'Deputy Superintendent of Police (DSP)'
    ],
    'Police Station Officers (PI, SI, PSI)': [
      'Police Inspector (PI)',
      'Sub-Inspector (SI)',
      'Police Sub-Inspector (PSI)'
    ],
    'Police Station Staff (HC, PC)': [
      'Head Constable (HC)',
      'Police Constable (PC)'
    ]
  };

  useEffect(() => {
    fetchUsers();
  }, [currentPage, searchTerm, roleFilter]);

  const fetchUsers = async () => {
    try {
      setLoading(true);
      const response = await adminAPI.getUsers({
        page: currentPage,
        limit: 10,
        search: searchTerm,
        role: roleFilter
      });

      if (response.data.success) {
        setUsers(response.data.data.users);
        setTotalPages(response.data.data.pagination.pages);
      }
    } catch (error) {
      toast.error('Failed to fetch users');
    } finally {
      setLoading(false);
    }
  };

  const handleToggleStatus = async (userId, currentStatus) => {
    try {
      await adminAPI.updateUser(userId, { isActive: !currentStatus });
      toast.success(`User ${!currentStatus ? 'activated' : 'deactivated'} successfully`);
      fetchUsers();
    } catch (error) {
      toast.error('Failed to update user status');
    }
  };

  const handleCreateUser = async (e) => {
    e.preventDefault();
    try {
      const response = await adminAPI.createUser(newUser);
      if (response.data.success) {
        toast.success('Officer created successfully');
        setShowCreateModal(false);
        setNewUser({
          officerId: '',
          fullName: '',
          designation: '',
          email: '',
          dateOfJoining: '',
          password: '',
          rank: ''
        });
        fetchUsers();
      }
    } catch (error) {
      toast.error(error.response?.data?.message || 'Failed to create officer');
    }
  };

  const handleRankChange = (e) => {
    const selectedRank = e.target.value;
    setNewUser({
      ...newUser,
      rank: selectedRank,
      designation: '' // Reset designation when rank changes
    });
  };

  const getAvailableDesignations = () => {
    if (!newUser.rank) return [];
    return rankCategories[newUser.rank] || [];
  };

  if (loading) {
    return (
      /* UI/UX Enhancement: Styled by .loading-container from CSS */
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading users...</p>
      </div>
    );
  }

  return (
    <div className="user-management">
      <div className="section-header">
        <h2>User Management</h2>
        <button 
          className="btn btn-primary"
          onClick={() => setShowCreateModal(true)}
        >
          + Create New Officer
        </button>
      </div>

      {/* Filters Section */}
      <div className="search-filters">
        <input
          type="text"
          placeholder="Search by name, email, or officer ID..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="search-input"
        />
        <select
          value={roleFilter}
          onChange={(e) => setRoleFilter(e.target.value)}
          className="filter-select"
        >
          <option value="">All Roles</option>
          <option value="admin">Admin</option>
          <option value="officer">Officer</option>
        </select>
      </div>

      {/* Users Table */}
      {users.length === 0 ? (
        <div className="empty-state">
          <p>No users found.</p>
        </div>
      ) : (
        <div className="table-container">
          <table className="users-table">
            <thead>
              <tr>
                <th>Officer ID</th>
                <th>Full Name</th>
                <th>Email</th>
                <th>Designation</th>
                <th>Rank</th>
                <th>Years of Service</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {users.map(user => (
                <tr key={user._id}>
                  <td>{user.officerId}</td>
                  <td>{user.fullName}</td>
                  <td>{user.email}</td>
                  <td>
                    {/* UI/UX Enhancement: Styled by .badge and .badge-x */}
                    <span className="badge badge-info">{user.designation}</span>
                  </td>
                  <td>{user.rank}</td>
                  <td>{user.yearsOfService || 'N/A'}</td>
                  <td>
                    {/* UI/UX Enhancement: Styled by .badge and .badge-x */}
                    <span className={`badge ${user.isActive ? 'badge-success' : 'badge-error'}`}>
                      {user.isActive ? 'Active' : 'Inactive'}
                    </span>
                  </td>
                  <td>
                    {/* UI/UX Enhancement: Styled by .btn, .btn-sm, etc. */}
                    <button
                      className={`btn btn-sm ${user.isActive ? 'btn-warning' : 'btn-success'}`}
                      onClick={() => handleToggleStatus(user._id, user.isActive)}
                    >
                      {user.isActive ? 'Deactivate' : 'Activate'}
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {/* Pagination */}
      {totalPages > 1 && (
        <div className="pagination">
          <button
            className="btn btn-secondary"
            onClick={() => setCurrentPage(prev => Math.max(1, prev - 1))}
            disabled={currentPage === 1}
          >
            Previous
          </button>
          <span className="page-info">
            Page {currentPage} of {totalPages}
          </span>
          <button
            className="btn btn-secondary"
            onClick={() => setCurrentPage(prev => Math.min(totalPages, prev + 1))}
            disabled={currentPage === totalPages}
          >
            Next
          </button>
        </div>
      )}

      {/* Create User Modal */}
      {showCreateModal && (
        <div className="modal-overlay" onClick={() => setShowCreateModal(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>Create New Officer</h3>
              <button 
                className="modal-close"
                onClick={() => setShowCreateModal(false)}
              >
                Ã—
              </button>
            </div>
            
            <form onSubmit={handleCreateUser} className="modal-form">
              <div className="form-row">
                <div className="form-group">
                  <label>Officer ID *</label>
                  <input
                    type="text"
                    value={newUser.officerId}
                    onChange={(e) => setNewUser({...newUser, officerId: e.target.value.toUpperCase()})}
                    placeholder="e.g., OFF12345"
                    required
                    pattern="[A-Z0-9]{6,12}"
                    title="6-12 alphanumeric characters"
                  />
                </div>

                <div className="form-group">
                  <label>Full Name *</label>
                  <input
                    type="text"
                    value={newUser.fullName}
                    onChange={(e) => setNewUser({...newUser, fullName: e.target.value})}
                    placeholder="Enter full name"
                    required
                    minLength="3"
                    maxLength="100"
                  />
                </div>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>Email *</label>
                  <input
                    type="email"
                    value={newUser.email}
                    onChange={(e) => setNewUser({...newUser, email: e.target.value})}
                    placeholder="officer@police.gov.in"
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Date of Joining *</label>
                  <input
                    type="date"
                    value={newUser.dateOfJoining}
                    onChange={(e) => setNewUser({...newUser, dateOfJoining: e.target.value})}
                    required
                    max={new Date().toISOString().split('T')[0]}
                  />
                </div>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>Rank *</label>
                  <select
                    value={newUser.rank}
                    onChange={handleRankChange}
                    required
                  >
                    <option value="">Select Rank Category</option>
                    {Object.keys(rankCategories).map(rank => (
                      <option key={rank} value={rank}>{rank}</option>
                    ))}
                  </select>
                </div>

                <div className="form-group">
                  <label>Designation *</label>
                  <select
                    value={newUser.designation}
                    onChange={(e) => setNewUser({...newUser, designation: e.target.value})}
                    required
                    disabled={!newUser.rank}
                  >
                    <option value="">
                      {newUser.rank ? 'Select Designation' : 'Select Rank First'}
                    </option>
                    {getAvailableDesignations().map(designation => (
                      <option key={designation} value={designation}>
                        {designation}
                      </option>
                    ))}
                  </select>
                </div>
              </div>

              <div className="form-group">
                <label>Password *</label>
                <input
                  type="password"
                  value={newUser.password}
                  onChange={(e) => setNewUser({...newUser, password: e.target.value})}
                  placeholder="Minimum 8 characters"
                  required
                  minLength="8"
                />
              </div>

              <div className="modal-actions">
                <button 
                  type="button" 
                  className="btn btn-secondary"
                  onClick={() => setShowCreateModal(false)}
                >
                  Cancel
                </button>
                <button type="submit" className="btn btn-primary">
                  Create Officer
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default UserManagement;
