import React, { useState, useEffect } from 'react';
import { equipmentAPI } from '../../utils/api';
import { toast } from 'react-toastify';

const MaintenanceLog = () => {
  const [maintenanceItems, setMaintenanceItems] = useState([]);
  const [loading, setLoading] = useState(true);
  
  // State for the *two* different modals
  const [showRepairModal, setShowRepairModal] = useState(false);
  const [showLostModal, setShowLostModal] = useState(false);
  
  const [selectedItem, setSelectedItem] = useState(null);

  useEffect(() => {
    fetchMaintenanceItems();
  }, []);

  // ======== 游릭 MODIFIED THIS FUNCTION 游릭 ========
  const fetchMaintenanceItems = async () => {
    try {
      setLoading(true);
      // Call the new, dedicated API endpoint
      // You will need to add 'getMaintenanceItems' to your api.js file
      const response = await equipmentAPI.getMaintenanceItems(); 
      if (response.data.success) {
        // The API now returns a flat list of items, so we can set it directly
        setMaintenanceItems(response.data.data.items);
      }
    } catch (error) {
      toast.error('Failed to fetch maintenance list');
    } finally {
      setLoading(false);
    }
  };
  // =============================================

  // --- Handlers for "Repair" Modal ---
  const handleOpenRepairModal = (item) => {
    setSelectedItem(item);
    setShowRepairModal(true);
  };
  const handleCloseRepairModal = () => {
    setSelectedItem(null);
    setShowRepairModal(false);
  };

  // --- Handlers for "Lost" Modal ---
  const handleOpenLostModal = (item) => {
    setSelectedItem(item);
    setShowLostModal(true);
  };
  const handleCloseLostModal = () => {
    setSelectedItem(null);
    setShowLostModal(false);
  };

  // --- Generic success handler to refresh list ---
  const handleSuccess = () => {
    handleCloseRepairModal();
    handleCloseLostModal();
    fetchMaintenanceItems(); 
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading items under maintenance...</p>
      </div>
    );
  }

  return (
    <>
      <div className="maintenance-log">
        <div className="management-header">
          <h3>Maintenance Log</h3>
          <p>Items currently out of service and awaiting repair or investigation.</p>
        </div>

        {maintenanceItems.length === 0 ? (
          <div className="no-data">
            <p>No items are currently under maintenance.</p>
          </div>
        ) : (
          <div className="inventory-table">
            <table className="table">
              <thead>
                <tr>
                  <th>Item ID</th>
                  <th>Pool Name</th>
                  <th>Problem Reported</th>
                  <th>Status</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {maintenanceItems.map((item) => {
                  // This logic is now safe because the new API sends the full history
                  const problemReport = item.maintenanceHistory && item.maintenanceHistory.length > 0
                    ? item.maintenanceHistory[item.maintenanceHistory.length - 1]
                    : {};
                  let reason = problemReport?.reason || 'N/A';
                  
                  const isLost = reason.startsWith("ITEM REPORTED LOST");

                  return (
                    <tr key={item.uniqueId}>
                      <td><strong>{item.uniqueId}</strong></td>
                      <td>{item.poolName}</td>
                      <td title={reason}>
                        {reason.length > 70 ? `${reason.substring(0, 70)}...` : reason}
                      </td>
                      <td>
                        {isLost ? (
                          <span className="badge badge-danger">LOST (Pending)</span>
                        ) : (
                          <span className="badge badge-warning">{item.condition}</span>
                        )}
                      </td>
                      <td>
                        {isLost ? (
                          <button
                            onClick={() => handleOpenLostModal(item)}
                            className="btn btn-sm btn-warning" 
                          >
                            Manage Report
                          </button>
                        ) : (
                          <button
                            onClick={() => handleOpenRepairModal(item)}
                            className="btn btn-sm btn-success"
                          >
                            Complete Repair
                          </button>
                        )}
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        )}
      </div>

      {/* --- Render Repair Modal --- */}
      {showRepairModal && selectedItem && (
        <CompleteRepairModal
          item={selectedItem}
          onClose={handleCloseRepairModal}
          onSuccess={handleSuccess}
        />
      )}
      
      {/* --- Render NEW Lost Modal (This will now have the correct data) --- */}
      {showLostModal && selectedItem && (
        <ManageLostItemModal
          item={selectedItem}
          onClose={handleCloseLostModal}
          onSuccess={handleSuccess}
        />
      )}
    </>
  );
};

// ===================================================================
// ======== 游릭 MODAL COMPONENT FOR LOST ITEMS 游릭 ========
// ===================================================================
// This component is now guaranteed to receive the full 'item.lostHistory'
// because the new API endpoint provides it.
const ManageLostItemModal = ({ item, onClose, onSuccess }) => {
  const [formData, setFormData] = useState({
    action: '', // 'recovered' or 'writeoff'
    notes: '',
    condition: 'Good' // Only used if 'recovered'
  });
  const [loading, setLoading] = useState(false);

  // This will now work correctly
  const lostReport = item.lostHistory && item.lostHistory.length > 0 
    ? item.lostHistory[item.lostHistory.length - 1] 
    : {};

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.action) {
      toast.error('Please select an action.');
      return;
    }
    if (formData.notes.length < 10) {
      toast.error('Please provide detailed final notes (min 10 chars).');
      return;
    }
    
    setLoading(true);
    
    const payload = {
      poolId: item.poolId,
      uniqueId: item.uniqueId,
      notes: formData.notes,
    };

    try {
      if (formData.action === 'recovered') {
        // This API call correctly sets status to 'Available'
        await equipmentAPI.markAsRecovered({
          ...payload,
          condition: formData.condition,
        });
        toast.success(`Item ${item.uniqueId} marked as recovered.`);
      } else if (formData.action === 'writeoff') {
        // This API call correctly sets status to 'Lost'
        await equipmentAPI.writeOffLost(payload);
        toast.success(`Item ${item.uniqueId} has been written off as "Lost".`);
      }
      onSuccess();
    } catch (error) {
      toast.error('Failed to process lost item report.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content modal-lg" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h3>Manage Lost Item Report: {item.uniqueId}</h3>
          <button onClick={onClose} className="close-btn">&times;</button>
        </div>
        <form onSubmit={handleSubmit}>
          <div className="modal-body">
            
            <div className="form-note form-note-danger">
              <p><strong>Item Status:</strong> LOST (Under Investigation)</p>
            </div>
            
            <h4 className="form-section-header">Officer's Report</h4>
            <div className="detail-grid-admin">
              <div className="detail-item-admin">
                <strong>FIR Number:</strong> {lostReport.firNumber || 'N/A'}
              </div>
              <div className="detail-item-admin">
                <strong>FIR Date:</strong> {lostReport.firDate ? new Date(lostReport.firDate).toLocaleDateString() : 'N/A'}
              </div>
              <div className="detail-item-admin">
                <strong>Police Station:</strong> {lostReport.policeStation || 'N/A'}
              </div>
              <div className="detail-item-admin">
                <strong>Date of Loss:</strong> {lostReport.dateOfLoss ? new Date(lostReport.dateOfLoss).toLocaleString() : 'N/A'}
              </div>
              <div className="detail-item-admin">
                <strong>Place of Loss:</strong> {lostReport.placeOfLoss || 'N/A'}
              </div>
              <div className="detail-item-admin">
                <strong>Duty at Time:</strong> {lostReport.dutyAtTimeOfLoss || 'N/A'}
              </div>
            </div>
            <div className="detail-item-admin-full">
              <strong>Incident Details:</strong>
              <p>{lostReport.description || 'N/A'}</p>
            </div>
            <div className="detail-item-admin-full">
              <strong>Officer's Actions:</strong>
              <p>{lostReport.remedialActionTaken || 'N/A'}</p>
            </div>

            <h4 className="form-section-header">Admin Final Action</h4>
            <div className="form-group">
              <label className="form-label">Select Action <span className="required">*</span></label>
              <select
                name="action"
                value={formData.action}
                onChange={handleChange}
                className="form-control"
              >
                <option value="">-- Select Final Action --</option>
                <option value="recovered">Mark as Recovered</option>
                <option value="writeoff">Write-off as Lost</option>
              </select>
            </div>

            {/* --- Conditional Field: Show if "Recovered" --- */}
            {formData.action === 'recovered' && (
              <div className="form-group">
                <label className="form-label">New Condition <span className="required">*</span></label>
                <select
                  name="condition"
                  value={formData.condition}
                  onChange={handleChange}
                  className="form-control"
                >
                  <option value="Good">Good</option>
                  <option value="Excellent">Excellent</option>
                  <option value="Fair">Fair</option>
                  <option value="Poor">Poor (Will stay in maintenance)</option>
                </select>
              </div>
            )}
            
            <div className="form-group">
              <label className="form-label">Final Notes / Report Summary <span className="required">*</span></label>
              <textarea
                name="notes"
                value={formData.notes}
                onChange={handleChange}
                className="form-control"
                rows="3"
                placeholder="e.g., 'Item recovered from suspect. Case closed.' OR 'Investigation concluded, item is non-recoverable. Writing off.'"
                required
              />
            </div>

          </div>
          <div className="modal-actions">
            <button type="button" onClick={onClose} className="btn btn-secondary">
              Cancel
            </button>
            <button type="submit" className="btn btn-success" disabled={loading}>
              {loading ? 'Saving...' : 'Submit Final Report'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};


// ... (CompleteRepairModal component is unchanged) ...
const CompleteRepairModal = ({ item, onClose, onSuccess }) => {
  const [formData, setFormData] = useState({
    description: '',
    condition: 'Good',
    cost: ''
  });
  const [loading, setLoading] = useState(false);

  const problemReportEntry = item.maintenanceHistory[item.maintenanceHistory.length - 1];
  let problemReason = problemReportEntry?.reason || 'No description provided.';

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData.description.length < 5) {
      toast.error('Please describe the repair actions taken.');
      return;
    }
    
    setLoading(true);
    try {
      const payload = {
        poolId: item.poolId,
        uniqueId: item.uniqueId,
        description: formData.description,
        condition: formData.condition,
        cost: formData.cost || 0
      };

      await equipmentAPI.completeMaintenance(payload);
      toast.success(`Item ${item.uniqueId} has been repaired and is now Available.`);
      onSuccess();
    } catch (error) {
      toast.error('Failed to complete repair.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h3>Complete Repair for: {item.uniqueId}</h3>
          <button onClick={onClose} className="close-btn">&times;</button>
        </div>
        <form onSubmit={handleSubmit}>
          <div className="modal-body">
            
            <div className="form-group">
              <label className="form-label">Problem Reported by Officer</label>
              <textarea
                className="form-control"
                rows="3"
                value={problemReason}
                readOnly
              />
            </div>
            
            <div className="form-group">
              <label className="form-label">Repair Actions Taken <span className="required">*</span></label>
              <textarea
                name="description"
                value={formData.description}
                onChange={handleChange}
                className="form-control"
                rows="3"
                placeholder="e.g., 'Replaced firing pin, cleaned and tested.'"
                required
              />
            </div>

            <div className="form-row">
              <div className="form-group">
                <label className="form-label">New Condition</label>
                <select
                  name="condition"
                  value={formData.condition}
                  onChange={handleChange}
                  className="form-control"
                >
                  <option value="Good">Good</option>
                  <option value="Excellent">Excellent</option>
                </select>
              </div>
              <div className="form-group">
                <label className="form-label">Cost of Repair (Optional)</label>
                <input
                  type="number"
                  name="cost"
                  value={formData.cost}
                  onChange={handleChange}
                  className="form-control"
                  placeholder="e.g., 500"
                />
              </div>
            </div>
          </div>
          <div className="modal-actions">
            <button type="button" onClick={onClose} className="btn btn-secondary">
              Cancel
            </button>
            <button type="submit" className="btn btn-success" disabled={loading}>
              {loading ? 'Saving...' : 'Complete Repair'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default MaintenanceLog;
