const LostModal = ({ equipment, onClose, onSuccess }) => {
  const [formData, setFormData] = useState({
    reason: '', // This is the "description"
    firNumber: '',
    firDate: '',
    policeStation: '', 
    dateOfLoss: '',
    placeOfLoss: '',
    dutyAtTimeOfLoss: '',
    remedialActionTaken: '', // <-- 1. ADDED state for remedial action
    condition: 'Lost', // Hardcoded
    priority: 'Urgent' // Hardcoded
  });
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // <-- 2. UPDATED validation check
    if (!formData.firNumber || !formData.firDate || !formData.policeStation || 
        !formData.dateOfLoss || !formData.placeOfLoss || !formData.dutyAtTimeOfLoss ||
        !formData.remedialActionTaken) { // <-- Added to check
      toast.error('All fields are required. Please fill out the entire form.');
      return;
    }
    if (formData.reason.length < 10) {
      toast.error('Please describe the incident in detail (min 10 chars).');
      return;
    }
    setLoading(true);

    try {
      // <-- 3. UPDATED API payload
      await officerAPI.createRequest({
        poolId: equipment.poolId,
        uniqueId: equipment._id,
        requestType: 'Lost',
        reason: formData.reason,
        priority: formData.priority,
        condition: formData.condition,
        firNumber: formData.firNumber,
        firDate: formData.firDate,
        policeStation: formData.policeStation,
        dateOfLoss: formData.dateOfLoss,
        placeOfLoss: formData.placeOfLoss,
        dutyAtTimeOfLoss: formData.dutyAtTimeOfLoss,
        remedialActionTaken: formData.remedialActionTaken // <-- Sent to API
      });
      onSuccess(); // Will show toast in parent
    } catch (error) {
      toast.error('Failed to submit lost report');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
