// ======== ðŸŸ¢ NEW DETAILED LOST MODAL ðŸŸ¢ ========
const LostModal = ({ equipment, onClose, onSuccess }) => {
  const [formData, setFormData] = useState({
    reason: '', // This is now 'Incident Details'
    firNumber: '',
    firDate: '',
    policeStation: '',
    dateOfLoss: '',
    placeOfLoss: '',
    dutyAtTimeOfLoss: '',
    remedialActionTaken: '',
    witnesses: '',
    condition: 'Lost',
    priority: 'Urgent'
  });
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Detailed Validation
    const requiredFields = [
      'firNumber', 'firDate', 'policeStation', 'dateOfLoss', 'placeOfLoss', 'dutyAtTimeOfLoss', 'reason'
    ];
    for (const field of requiredFields) {
      if (!formData[field] || formData[field].trim() === '') {
        toast.error(`Field "${field.replace(/([A-Z])/g, ' $1')}" is required.`);
        return;
      }
    }
    if (formData.reason.length < 20) {
      toast.error('Incident Details must be at least 20 characters long.');
      return;
    }

    setLoading(true);
    try {
      await officerAPI.createRequest({
        poolId: equipment.poolId,
        uniqueId: equipment._id,
        requestType: 'Lost',
        ...formData // Send all new fields
      });
      onSuccess();
    } catch (error) {
      toast.error('Failed to submit lost report. Please check all fields.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      {/* Use a larger modal for this form */}
      <div className="modal-content modal-lg" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h3>Report Lost Item: {equipment.name}</h3>
          <button onClick={onClose} className="close-btn">&times;</button>
        </div>

        <form onSubmit={handleSubmit}>
          <div className="modal-body">
            <div className="equipment-summary">
              <p>{equipment.model} - ID: {equipment.serialNumber}</p>
            </div>
            <div className="form-note form-note-danger">
              <p><strong>Warning:</strong> You are filing a formal report for a lost item. This will initiate an official investigation. Please be as accurate as possible.</p>
            </div>

            {/* --- Incident Details --- */}
            <h4 className="form-section-header">Incident Details</h4>
            <div className="form-row">
              <div className="form-group">
                <label className="form-label">Approx. Date/Time of Loss <span className="required">*</span></label>
                <input
                  type="datetime-local"
                  name="dateOfLoss"
                  value={formData.dateOfLoss}
                  onChange={handleChange}
                  className="form-control"
                  required
                />
              </div>
              <div className="form-group">
                <label className="form-label">Place of Loss <span className="required">*</span></label>
                <input
                  type="text"
                  name="placeOfLoss"
                  value={formData.placeOfLoss}
                  onChange={handleChange}
                  className="form-control"
                  placeholder="e.g., Sector 17 Market, near bus stand"
                  required
                />
              </div>
            </div>
            <div className="form-group">
              <label className="form-label">Duty at Time of Loss <span className="required">*</span></label>
              <input
                type="text"
                name="dutyAtTimeOfLoss"
                value={formData.dutyAtTimeOfLoss}
                onChange={handleChange}
                className="form-control"
                placeholder="e.g., Night patrol, Riot control, Off-duty"
                required
              />
            </div>
            <div className="form-group">
              <label className="form-label">Detailed Description of Incident <span className="required">*</span></label>
              <textarea
                name="reason" // This maps to 'reason' in the schema
                value={formData.reason}
                onChange={handleChange}
                className="form-control"
                rows="4"
                placeholder="Describe exactly what happened, how you noticed the loss, etc. (min 20 chars)"
                required
              />
            </div>

            {/* --- Official Report Details --- */}
            <h4 className="form-section-header">Official Report (FIR)</h4>
            <div className="form-row">
              <div className="form-group">
                <label className="form-label">FIR Number <span className="required">*</span></label>
                <input
                  type="text"
                  name="firNumber"
                  value={formData.firNumber}
                  onChange={handleChange}
                  className="form-control"
                  placeholder="e.g., 0123/2025"
                  required
                />
              </div>
              <div className="form-group">
                <label className="form-label">FIR Date <span className="required">*</span></label>
                <input
                  type="date"
                  name="firDate"
                  value={formData.firDate}
                  onChange={handleChange}
                  className="form-control"
                  required
                />
              </div>
              <div className="form-group">
                <label className="form-label">Police Station <span className="required">*</span></label>
                <input
                  type="text"
                  name="policeStation"
                  value={formData.policeStation}
                  onChange={handleChange}
                  className="form-control"
                  placeholder="e.g., PS Sector 17, Chandigarh"
                  required
                />
              </div>
            </div>

            {/* --- Follow-up Details --- */}
            <h4 className="form-section-header">Follow-up Actions</h4>
            <div className="form-group">
              <label className="form-label">Immediate Actions Taken <span className="required">*</span></label>
              <textarea
                name="remedialActionTaken"
                value={formData.remedialActionTaken}
                onChange={handleChange}
                className="form-control"
                rows="2"
                placeholder="e.g., Informed Duty Officer, searched the area for 30 minutes."
                required
              />
            </div>
            <div className="form-group">
              <label className="form-label">Witnesses (if any)</label>
              <input
                type="text"
                name="witnesses"
                value={formData.witnesses}
                onChange={handleChange}
                className="form-control"
                placeholder="e.g., HC Ram Singh (Badge 1234)"
              />
            </div>
            
          </div>
          <div className="modal-actions">
            <button type="button" onClick={onClose} className="btn btn-secondary">
              Cancel
            </button>
            <button type="submit" className="btn btn-danger" disabled={loading}>
              {loading ? 'Submitting Report...' : 'Submit Formal Report'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

