const getStatusBadgeClass = (status) => {
  const classes = {
    'Pending': 'warning',
    'Approved': 'success',
    'Rejected': 'danger',
    'Completed': 'info',
    'Cancelled': 'secondary'
  };
  return classes[status] || 'secondary';
};

const getPriorityBadgeClass = (priority) => {
  const classes = {
    'Low': 'info',
    'Medium': 'warning',
    'High': 'danger',
    'Urgent': 'danger'
  };
  return classes[priority] || 'secondary';
};

