import React, { useState, useEffect } from 'react';
import { adminAPI } from '../../utils/api';
import { equipmentAPI } from '../../utils/api';
import { toast } from 'react-toastify';

/*
  UI/UX Enhancement: This component is now styled by AdminDashboard.css.
  - The filters (.search-filters) are cleaner.
  - The table (.requests-table) is more professional.
  - The loading state (.loading-container) is themed.
  - Pagination (.pagination) is restyled.
  - Badges (.badge) are now styled with modern, readable colors.
  - NEW: Modal forms for approval/rejection replace prompt() dialogs
  - NEW: When a Return request is approved, equipment status is updated to "Available"
*/

const ProcessRequests = () => {
  const [requests, setRequests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [statusFilter, setStatusFilter] = useState('Pending');
  const [typeFilter, setTypeFilter] = useState('');
  
  // Modal states for approval and rejection
  const [showApprovalModal, setShowApprovalModal] = useState(false);
  const [showRejectionModal, setShowRejectionModal] = useState(false);
  const [selectedRequest, setSelectedRequest] = useState(null);

  useEffect(() => {
    fetchRequests();
  }, [currentPage, statusFilter, typeFilter]);

  const fetchRequests = async () => {
    try {
      setLoading(true);
      const response = await adminAPI.getRequests({
        page: currentPage,
        limit: 10,
        status: statusFilter,
        requestType: typeFilter
      });

      if (response.data.success) {
        setRequests(response.data.data.requests);
        setTotalPages(response.data.data.pagination.pages);
      }
    } catch (error) {
      toast.error('Failed to fetch requests');
    } finally {
      setLoading(false);
    }
  };

  const handleApproveClick = (request) => {
    setSelectedRequest(request);
    setShowApprovalModal(true);
  };

  const handleRejectClick = (request) => {
    setSelectedRequest(request);
    setShowRejectionModal(true);
  };

  const handleCloseModals = () => {
    setShowApprovalModal(false);
    setShowRejectionModal(false);
    setSelectedRequest(null);
  };

  if (loading) {
    return (
      /* UI/UX Enhancement: Styled by .loading-container from CSS */
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading requests...</p>
      </div>
    );
  }

  return (
    <>
      <div className="process-requests">
        <div className="management-header">
          {/* UI/UX Enhancement: Styled by .search-filters and .form-control */}
          <div className="search-filters">
            <select
              value={statusFilter}
              onChange={(e) => {
                setStatusFilter(e.target.value);
                setCurrentPage(1);
              }}
              className="form-control"
            >
              <option value="">All Status</option>
              <option value="Pending">Pending</option>
              <option value="Approved">Approved</option>
              <option value="Rejected">Rejected</option>
              <option value="Completed">Completed</option>
              <option value="Cancelled">Cancelled</option>
            </select>

            <select
              value={typeFilter}
              onChange={(e) => {
                setTypeFilter(e.target.value);
                setCurrentPage(1);
              }}
              className="form-control"
            >
              <option value="">All Types</option>
              <option value="Checkout">Checkout</option>
              <option value="Return">Return</option>
              <option value="Damage Report">Damage Report</option>
            </select>
          </div>
        </div>

        {requests.length === 0 ? (
          /* UI/UX Enhancement: Styled by .no-data from CSS */
          <div className="no-data">
            <p>No requests found.</p>
          </div>
        ) : (
          <>
            {/* UI/UX Enhancement: Styled by .requests-table and .table */}
            <div className="requests-table">
              <table className="table">
                <thead>
                  <tr>
                    <th>Request ID</th>
                    <th>Officer</th>
                    <th>Equipment</th>
                    <th>Type</th>
                    <th>Priority</th>
                    <th>Date</th>
                    <th>Status</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {requests.map((request) => (
                    <React.Fragment key={request._id}>
                      <tr>
                        <td>{request._id.slice(-8).toUpperCase()}</td>
                        <td>{request.officer?.firstName} {request.officer?.lastName}</td>
                        <td>{request.equipment?.name}</td>
                        <td>
                          <span className={`badge badge-${request.requestType === 'Checkout' ? 'info' : 'warning'}`}>
                            {request.requestType}
                          </span>
                        </td>
                        <td>
                          <span className={`badge badge-${getPriorityBadgeClass(request.priority)}`}>
                            {request.priority}
                          </span>
                        </td>
                        <td>{new Date(request.createdAt).toLocaleDateString()}</td>
                        <td>
                          <span className={`badge badge-${getStatusBadgeClass(request.status)}`}>
                            {request.status}
                          </span>
                        </td>
                        <td>
                          {request.status === 'Pending' && (
                            <>
                              <button
                                onClick={() => handleApproveClick(request)}
                                className="btn btn-sm btn-success"
                              >
                                Approve
                              </button>
                              <button
                                onClick={() => handleRejectClick(request)}
                                className="btn btn-sm btn-danger"
                              >
                                Reject
                              </button>
                            </>
                          )}
                        </td>
                      </tr>
                      {request.reason && (
                        <tr className="expandable-row">
                          <td colSpan="8">
                            <div className="request-details">
                              {request.reason && (
                                <p><strong>Reason:</strong> {request.reason}</p>
                              )}
                              {request.expectedReturnDate && (
                                <p><strong>Expected Return:</strong> {new Date(request.expectedReturnDate).toLocaleDateString()}</p>
                              )}
                              {request.adminNotes && (
                                <p><strong>Admin Notes:</strong> {request.adminNotes}</p>
                              )}
                              {request.processedBy && (
                                <p><strong>Processed By:</strong> {request.processedBy.firstName} {request.processedBy.lastName}</p>
                              )}
                            </div>
                          </td>
                        </tr>
                      )}
                    </React.Fragment>
                  ))}
                </tbody>
              </table>
            </div>

            {/* UI/UX Enhancement: Styled by .pagination from CSS */}
            {totalPages > 1 && (
              <div className="pagination">
                <button
                  onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                  disabled={currentPage === 1}
                  className="btn btn-secondary"
                >
                  Previous
                </button>
                <span>Page {currentPage} of {totalPages}</span>
                <button
                  onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
                  disabled={currentPage === totalPages}
                  className="btn btn-secondary"
                >
                  Next
                </button>
              </div>
            )}
          </>
        )}
      </div>

      {/* Approval Modal */}
      {showApprovalModal && selectedRequest && (
        <ApprovalModal
          request={selectedRequest}
          onClose={handleCloseModals}
          onSuccess={() => {
            fetchRequests();
            handleCloseModals();
          }}
        />
      )}

      {/* Rejection Modal */}
      {showRejectionModal && selectedRequest && (
        <RejectionModal
          request={selectedRequest}
          onClose={handleCloseModals}
          onSuccess={() => {
            fetchRequests();
            handleCloseModals();
          }}
        />
      )}
    </>
  );
};

/*
  UI/UX Enhancement: Approval Modal Component
  - Styled by .modal-overlay, .modal-content, .modal-header, .close-btn
  - Professional form with .form-group, .form-label, .form-control
  - Themed footer with .modal-actions
  - NEW: Updates equipment status to "Available" if it's a Return request
*/
const ApprovalModal = ({ request, onClose, onSuccess }) => {
  const [formData, setFormData] = useState({
    notes: ''
  });
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      // Approve the request
      await adminAPI.approveRequest(request._id, formData);
      toast.success('Request approved successfully');

      // If it's a Return request, update equipment status to Available
      if (request.requestType === 'Return' && request.equipment?._id) {
        try {
          await equipmentAPI.updateEquipment(request.equipment._id, {
            status: 'Available'
          });
          toast.success('Equipment marked as Available');
        } catch (error) {
          console.error('Failed to update equipment status:', error);
          toast.warning('Request approved, but failed to update equipment status');
        }
      }

      onSuccess();
    } catch (error) {
      toast.error('Failed to approve request');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h3>Approve Request</h3>
          <button onClick={onClose} className="close-btn">&times;</button>
        </div>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label className="form-label">Approval Notes (Optional)</label>
            <textarea
              name="notes"
              value={formData.notes}
              onChange={handleChange}
              className="form-control"
              rows="4"
              placeholder="Add any additional notes for this approval..."
            />
          </div>

          {request.requestType === 'Return' && (
            <div className="form-group">
              <p style={{ color: '#28a745', fontSize: '0.9em', margin: '10px 0' }}>
                âœ“ Equipment will be marked as <strong>Available</strong> for other officers to request
              </p>
            </div>
          )}

          <div className="modal-actions">
            <button
              type="button"
              onClick={onClose}
              className="btn btn-secondary"
            >
              Cancel
            </button>
            <button
              type="submit"
              className="btn btn-success"
              disabled={loading}
            >
              {loading ? 'Approving...' : 'Approve Request'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

/*
  UI/UX Enhancement: Rejection Modal Component
  - Styled by .modal-overlay, .modal-content, .modal-header, .close-btn
  - Professional form with required reason field
  - Themed footer with .modal-actions
*/
const RejectionModal = ({ request, onClose, onSuccess }) => {
  const [formData, setFormData] = useState({
    reason: '',
    adminNotes: ''
  });
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.reason.trim()) {
      toast.error('Rejection reason is required');
      return;
    }

    setLoading(true);

    try {
      await adminAPI.rejectRequest(request._id, formData);
      toast.success('Request rejected successfully');
      onSuccess();
    } catch (error) {
      toast.error('Failed to reject request');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h3>Reject Request</h3>
          <button onClick={onClose} className="close-btn">&times;</button>
        </div>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label className="form-label">Reason for Rejection <span style={{ color: 'red' }}>*</span></label>
            <textarea
              name="reason"
              value={formData.reason}
              onChange={handleChange}
              className="form-control"
              rows="3"
              placeholder="Please provide a detailed reason for rejection..."
              required
            />
          </div>

          <div className="form-group">
            <label className="form-label">Admin Notes (Optional)</label>
            <textarea
              name="adminNotes"
              value={formData.adminNotes}
              onChange={handleChange}
              className="form-control"
              rows="3"
              placeholder="Add any additional internal notes..."
            />
          </div>

          <div className="modal-actions">
            <button
              type="button"
              onClick={onClose}
              className="btn btn-secondary"
            >
              Cancel
            </button>
            <button
              type="submit"
              className="btn btn-danger"
              disabled={loading}
            >
              {loading ? 'Rejecting...' : 'Reject Request'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

const getStatusBadgeClass = (status) => {
  const classes = {
    'Pending': 'warning',
    'Approved': 'success',
    'Rejected': 'danger',
    'Completed': 'info',
    'Cancelled': 'secondary'
  };
  return classes[status] || 'secondary';
};

const getPriorityBadgeClass = (priority) => {
  const classes = {
    'Low': 'info',
    'Medium': 'warning',
    'High': 'danger',
    'Urgent': 'danger'
  };
  return classes[priority] || 'secondary';
};

export default ProcessRequests;
