import React, { useState } from 'react';
import { Navigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import './Login.css';

const Login = () => {
  // Removed 'isRegistering' state
  const [formData, setFormData] = useState({
    username: '',
    password: '',
  });
  const [isLoading, setIsLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false); // Added for password visibility
  const { login, isAuthenticated, user } = useAuth(); // Removed 'register'

  // Redirect if already authenticated
  if (isAuthenticated && user) {
    return <Navigate to={user.role === 'admin' ? '/admin' : '/officer'} replace />;
  }

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  // Simplified handleSubmit to only handle login
  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    await login({
      username: formData.username,
      password: formData.password
    });

    setIsLoading(false);
  };

  // Removed switchMode function

  return (
    <div className="login-wrapper">
      {/* Navbar */}
      <nav className="login-navbar">
        <div className="navbar-container">
          <div className="logo-container">
            <svg className="logo-icon" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path d="M12 22s8-4 8-10V5l-8-3-8 3v7c0 6 8 10 8 10z"/>
            </svg>
            <span className="logo-text">Police Inventory</span>
          </div>
        </div>
      </nav>

      {/* Main Container */}
      <div className="login-container">
        {/* Page Header (Updated Text) */}
        <div className="page-header">
          <h1 className="page-title">
            Sign in
          </h1>
          <p className="page-subtitle">
            Access your department dashboard.
          </p>
        </div>

        {/* Login Card */}
        <div className="login-card">
          <div className="card-header">
            <h2 className="card-title">
              Secure Login
            </h2>
            <p className="card-subtitle">
              Use your official department email.
            </p>
          </div>

          <form onSubmit={handleSubmit} className="login-form">
            {/* Removed all registration fields */}

            {/* Kept the original "Email" field, which is the username */}
            <div className="form-group">
              <label className="form-label">Email</label>
              <div className="input-wrapper">
                <svg className="input-icon" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                  <rect x="2" y="4" width="20" height="16" rx="2"/>
                  <path d="m22 7-8.97 5.7a1.94 1.94 0 0 1-2.06 0L2 7"/>
                </svg>
                <input
                  type="text"
                  name="username"
                  className="form-control with-icon"
                  value={formData.username}
                  onChange={handleChange}
                  required
                  placeholder="name@agency.gov"
                />
              </div>
            </div>

            {/* Removed the registration-only "Username" field */}

            <div className="form-group">
              <label className="form-label">Password</label>
              <div className="input-wrapper">
                <svg className="input-icon" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                  <rect x="3" y="11" width="18" height="11" rx="2" ry="2"/>
                  <path d="M7 11V7a5 5 0 0 1 10 0v4"/>
                </svg>
                <input
                  // Updated type to be dynamic
                  type={showPassword ? 'text' : 'password'}
                  name="password"
                  // Added a 'password-input' class for specific styling
                  className="form-control with-icon password-input"
                  value={formData.password}
                  onChange={handleChange}
                  required
                  placeholder="••••••••"
                />
                {/* Added password visibility toggle button */}
                <button
                  type="button"
                  className="password-toggle-btn"
                  onClick={() => setShowPassword(!showPassword)}
                  aria-label={showPassword ? "Hide password" : "Show password"}
                >
                  {showPassword ? (
                    // Eye-off icon
                    <svg viewBox="0 0 24 24" fill="none" stroke="currentColor">
                      <path d="M17.94 17.94A10.07 10.07 0 0 1 12 20c-7 0-11-8-11-8a18.45 18.45 0 0 1 5.06-5.94M9.9 4.24A9.12 9.12 0 0 1 12 4c7 0 11 8 11 8a18.5 18.5 0 0 1-2.16 3.19m-6.72-1.07a3 3 0 1 1-4.24-4.24M1 1l22 22"/>
                    </svg>
                  ) : (
                    // Eye-on icon
                    <svg viewBox="0 0 24 24" fill="none" stroke="currentColor">
                      <path d="M1 12s4-8 11-8 11 8 11 8-4 8-11 8-11-8-11-8z"/>
                      <circle cx="12" cy="12" r="3"/>
                    </svg>
                  )}
                </button>
              </div>
            </div>

            {/* Removed "Remember me" checkbox */}

            <button
              type="submit"
              className="btn-primary"
              disabled={isLoading}
            >
              {isLoading ? (
                <>
                  <span className="spinner"></span>
                  {'Signing in...'}
                </>
              ) : (
                <>
                  <span className="btn-arrow">→</span>
                  {'Sign in'}
                </>
              )}
            </button>
          </form>

          {/* Removed "Auth Switch" section */}
        </div>
      </div>

      {/* Footer */}
      <footer className="login-footer">
        <p className="footer-text">© 2025 Police Inventory. All rights reserved.</p>
        <div className="footer-links">
          <a href="#privacy" className="footer-link">Privacy</a>
          <a href="#terms" className="footer-link">Terms</a>
        </div>
      </footer>
    </div>
  );
};

export default Login;
