/**
 * Calculates the years of service based on the joining date.
 * @param {string} dateOfJoining - The ISO date string of joining.
 * @returns {string} - A string like "5.2 years" or "N/A".
 */
const calculateYearsOfService = dateOfJoining => {
  if (!dateOfJoining) return 'N/A';
  const joiningDate = new Date(dateOfJoining);
  if (isNaN(joiningDate.getTime())) return 'N/A';
  const today = new Date();
  if (joiningDate > today) return '0.0 years';
  const diffTime = Math.abs(today - joiningDate);
  const diffYears = diffTime / (1000 * 60 * 60 * 24 * 365.25);
  return diffYears.toFixed(1) + ' years';
};
