import React, { useState, useEffect } from 'react';
import { equipmentAPI } from '../../utils/api';
import { toast } from 'react-toastify';

// A simple helper to format dates
const formatDateTime = (dateString) => {
  if (!dateString) return 'N/A';
  return new Date(dateString).toLocaleString('en-IN');
};

const ItemHistoryModal = ({ poolId, uniqueId, onClose }) => {
  // ======== 游릭 MODIFIED 游릭 ========
  // We now expect a single 'timeline' array and a details object
  const [timeline, setTimeline] = useState([]); 
  const [itemDetails, setItemDetails] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchHistory = async () => {
      try {
        setLoading(true);
        const response = await equipmentAPI.getItemHistory(poolId, uniqueId);
        if (response.data.success) {
          // Set state from the new API response
          setTimeline(response.data.data.timeline); 
          setItemDetails({
            currentStatus: response.data.data.currentStatus,
            currentCondition: response.data.data.currentCondition
          });
        } else {
          toast.error(response.data.message);
        }
      } catch (error) {
        toast.error('Failed to fetch item history');
      } finally {
        setLoading(false);
      }
    };
    
    fetchHistory();
  }, [poolId, uniqueId]);

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content equipment-details-modal" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h3>History for Item: {uniqueId}</h3>
          <button onClick={onClose} className="close-btn">&times;</button>
        </div>
        <div className="modal-body">
          {loading ? (
            <div className="loading-container">
              <div className="spinner"></div>
              <p>Loading history...</p>
            </div>
          ) : !itemDetails ? ( // Check for itemDetails
            <p>Could not load history.</p>
          ) : (
            <>
              <div className="detail-section">
                <h4>Current Status</h4>
                <div className="detail-grid">
                  <div className="detail-item">
                    <strong>Status:</strong> {itemDetails.currentStatus}
                  </div>
                  <div className="detail-item">
                    <strong>Condition:</strong> {itemDetails.currentCondition}
                  </div>
                </div>
              </div>

              {/* ======== 游릭 MODIFIED THIS ENTIRE SECTION 游릭 ======== */}
              {/* This now reads from the single 'timeline' array */}
              <div className="detail-section">
                <h4>Item Timeline</h4>
                {/* This check is now safe because timeline is initialized to [] */}
                {timeline.length === 0 ? ( 
                  <p>No history found for this item. (Brand New)</p>
                ) : (
                  <table className="table table-sm">
                    <thead>
                      <tr>
                        <th>Date & Time</th>
                        <th>Event</th>
                        <th>Details</th>
                        <th>Condition</th>
                      </tr>
                    </thead>
                    <tbody>
                      {timeline.map(entry => {
                        let eventType, details, condition;
                        
                        // Set display values based on the event type from the API
                        if (entry.eventType === 'Usage') {
                          eventType = <span className="badge badge-info">Issued</span>;
                          details = `To: ${entry.officer} (${entry.purpose})`;
                          condition = `(at issue: ${entry.conditionAtIssue})`;
                        
                        } else if (entry.eventType === 'Return') {
                          eventType = <span className="badge badge-success">Returned</span>;
                          details = `By: ${entry.officerName || 'N/A'}`;
                          condition = (
                            <span className={`badge badge-${entry.conditionAtReturn === 'Good' || entry.conditionAtReturn === 'Excellent' ? 'success' : 'danger'}`}>
                              {entry.conditionAtReturn}
                            </span>
                          );
                        
                        } else { // Maintenance
                          eventType = <span className="badge badge-warning">Maintenance</span>;
                          details = entry.details; // This holds the problem/repair description
                          condition = `(By: ${entry.performedBy})`;
                        }
                        
                        return (
                          <tr key={entry._id}>
                            <td>{formatDateTime(entry.eventDate)}</td>
                            <td>{eventType}</td>
                            <td>{details}</td>
                            <td>{condition}</td>
                          </tr>
                        );
                      })}
                    </tbody>
                  </table>
                )}
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default ItemHistoryModal;
